mapper.select:
SELECT COUNT(*) FROM SERVICE
WHERE SERVICE_STATUS != 5
AND SERVICE_STATUS = #{status}

SELECT TO_CHAR(PAYMENT_DATE, 'YYYY"-"MM"-"DD') PAYMENT_DATE, TRADE_NO, SERVICE_TITLE, WORK_STATUS, MEMBER_NAME AS USER_NAME, PAYMENT_TYPE, TO_CHAR(PAYMENT_PRICE, '999,999,999') PAYMENT_PRICE FROM SETTLEMENT JOIN "MEMBER" ON (MEMBER.MEMBER_NO=SETTLEMENT.USER_NO) JOIN TRADE USING (TRADE_NO) JOIN SERVICE USING (SERVICE_NO)
WHERE WORK_STATUS= #{status}
ORDER BY SETTLEMENT_NO DESC

