mapper.select:
SELECT COUNT(*) FROM SETTLEMENT 
JOIN TRADE USING (TRADE_NO)
JOIN SERVICE USING (SERVICE_NO)
JOIN CATEGORY USING (THIRD_CATEGORY_NO)
JOIN SUB_CATEGORY USING (SUB_CATEGORY_NO)
JOIN MAIN_CATEGORY USING (MAIN_CATEGORY_NO)
WHERE USER_NO=#{loginMemberNo}
AND PAYMENT_TYPE=1

	AND MAIN_CATEGORY_NO = #{type}


	AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN #{searchDate1} AND #{searchDate2}


	AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') >= #{searchDate1}


	AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') <= #{searchDate2}

  SELECT USER_NO AS MEMBER_NO, SERVICE_TITLE, 
	MAIN_CATEGORY_NAME, TO_CHAR(PAYMENT_DATE, 'YYYY"-"MM"-"DD') PAYMENT_DATE, PAYMENT_PRICE,
	CASE WHEN PAYMENT_TYPE=1 THEN '구매'
	     WHEN PAYMENT_TYPE=3 THEN '환불' END AS PAYMENT_TYPE
	FROM SETTLEMENT
	JOIN TRADE USING (TRADE_NO)
	JOIN SERVICE USING (SERVICE_NO)
	JOIN CATEGORY USING (THIRD_CATEGORY_NO)
	JOIN SUB_CATEGORY USING (SUB_CATEGORY_NO)
	JOIN MAIN_CATEGORY USING (MAIN_CATEGORY_NO)
	WHERE USER_NO=#{loginMemberNo}

		AND MAIN_CATEGORY_NO = #{type}


		AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN #{searchDate1} AND #{searchDate2}


		AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') >= #{searchDate1}


		AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') <= #{searchDate2}

  SELECT COUNT(*) FROM TRADE 
	JOIN SERVICE USING (SERVICE_NO)
	JOIN SETTLEMENT ON (TRADE.TRADE_NO=SETTLEMENT.TRADE_NO)
	JOIN CATEGORY USING (THIRD_CATEGORY_NO)
	JOIN SUB_CATEGORY USING (SUB_CATEGORY_NO)
	JOIN MAIN_CATEGORY USING (MAIN_CATEGORY_NO)
	WHERE MEMBER_NO=#{loginMemberNo}

		AND MAIN_CATEGORY_NO = #{type}


		AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN #{searchDate1} AND #{searchDate2}


		AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') >= #{searchDate1}


		AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') <= #{searchDate2}


		AND SERVICE_TITLE LIKE '%${searchInput}%'

SELECT TRADE.TRADE_NO, SERVICE_NO, SERVICE_TITLE, SERVICE_STATUS, SERVICE_DEL_FL, (SELECT COUNT(*) FROM WORK WHERE WORK.TRADE_NO=TRADE.TRADE_NO) WORK_COUNT, SERVICE_EDIT_NUM, MEMBER_DONE_FL, FREELANCER_DONE_FL, MAIN_CATEGORY_NO, SUB_CATEGORY_NO, THIRD_CATEGORY_NO, FREELANCER_NO, WORK_STATUS, (SELECT MEMBER_NAME FROM "MEMBER" WHERE MEMBER_NO=FREELANCER_NO) FREELANCER_NAME, CASE WHEN MEMBER_DONE_FL=2 OR WORK_STATUS=2 OR WORK_STATUS=4 THEN '작업 완료' WHEN WORK_STATUS=3 THEN '주문 취소' ELSE '진행중' END AS WORK_PROGRESS, (SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER_NO=#{loginMemberNo}) AS MEMBER_NAME, (SELECT MEMBER_NO FROM MEMBER WHERE MEMBER_NO=#{loginMemberNo}) AS MEMBER_NO FROM TRADE JOIN SERVICE USING (SERVICE_NO) JOIN SETTLEMENT ON (TRADE.TRADE_NO=SETTLEMENT.TRADE_NO) JOIN CATEGORY USING (THIRD_CATEGORY_NO) JOIN SUB_CATEGORY USING (SUB_CATEGORY_NO) JOIN MAIN_CATEGORY USING (MAIN_CATEGORY_NO) WHERE MEMBER_NO=#{loginMemberNo} AND PAYMENT_TYPE=1
AND MAIN_CATEGORY_NO = #{type}
AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN #{searchDate1} AND #{searchDate2}
AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') >= #{searchDate1}
AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') <= #{searchDate2}
AND SERVICE_TITLE LIKE '%${searchInput}%'
