mapper.select:
SELECT COUNT(*) FROM (SELECT * FROM TRADE JOIN (SELECT * FROM SERVICE LEFT JOIN CATEGORY USING(THIRD_CATEGORY_NO) LEFT JOIN SUB_CATEGORY USING(SUB_CATEGORY_NO)) USING(SERVICE_NO) LEFT JOIN (SELECT TRADE_NO, NVL(WORK_COUNT,0) WORK_COUNT FROM TRADE LEFT JOIN (SELECT COUNT(*) WORK_COUNT,TRADE_NO FROM TRADE JOIN "WORK" USING(TRADE_NO) GROUP BY TRADE_NO) USING (TRADE_NO)) USING (TRADE_NO) JOIN (SELECT MEMBER_NO,MEMBER_NICKNAME,MEMBER_NAME FROM "MEMBER") USING(MEMBER_NO) JOIN "SETTLEMENT" USING(TRADE_NO) LEFT JOIN TRADE_REPORT USING(TRADE_NO) WHERE REPORT_PERSON = FREELANCER_NO OR REPORT_PERSON IS null) WHERE FREELANCER_NO = 30 AND PAYMENT_TYPE = 1 AND (TRADE_REPORT_TYPE_NO IS NULL OR TRADE_REPORT_TYPE_NO = 1)
AND MAIN_CATEGORY_NO = #{mainCategoryNo}
AND FREELANCER_DONE_FL = #{freelancerFL}
AND SERVICE_TITLE LIKE '%${searchInput}%'
ORDER BY TRADE_NO

SELECT t.*, CASE WHEN FREELANCER_DONE_FL = 1 THEN '진행 중' WHEN FREELANCER_DONE_FL = 2 THEN '작업 완료' WHEN FREELANCER_DONE_FL = 3 THEN '진행 취소' END AS FREELANCER_DONE_FL_STRING, CASE WHEN WORK_STATUS = 3 THEN '진행 취소' WHEN WORK_STATUS = 2 THEN '정산 완료' END AS WORK_STATUS_STRING FROM (SELECT * FROM TRADE JOIN (SELECT * FROM SERVICE LEFT JOIN CATEGORY USING(THIRD_CATEGORY_NO) LEFT JOIN SUB_CATEGORY USING(SUB_CATEGORY_NO)) USING(SERVICE_NO) LEFT JOIN (SELECT TRADE_NO, NVL(WORK_COUNT,0) WORK_COUNT FROM TRADE LEFT JOIN (SELECT COUNT(*) WORK_COUNT,TRADE_NO FROM TRADE JOIN "WORK" USING(TRADE_NO) GROUP BY TRADE_NO) USING (TRADE_NO)) USING (TRADE_NO) JOIN (SELECT MEMBER_NO,MEMBER_NICKNAME,MEMBER_NAME FROM "MEMBER") USING(MEMBER_NO) JOIN "SETTLEMENT" USING(TRADE_NO) LEFT JOIN TRADE_REPORT USING(TRADE_NO) WHERE REPORT_PERSON = FREELANCER_NO OR REPORT_PERSON IS null) t WHERE FREELANCER_NO = #{memberNo} AND PAYMENT_TYPE = 1 AND (TRADE_REPORT_TYPE_NO IS NULL OR TRADE_REPORT_TYPE_NO = 1)
AND MAIN_CATEGORY_NO = #{mainCategoryNo}
AND FREELANCER_DONE_FL = #{freelancerFL}
AND SERVICE_TITLE LIKE '%${searchInput}%'
ORDER BY TRADE_NO

mapper.update:
    UPDATE TRADE
		SET FREELANCER_DONE_FL = 2
		WHERE TRADE_NO = #{tradeNo}

mapper.select:
SELECT p.*,TO_CHAR(PROPOSAL_PRICE, 'FM999,999,999,999') PROPOSAL_PRICE_STRING, CASE WHEN PROPOSAL_ADOPT_STATUS = 1 THEN '대기중' WHEN PROPOSAL_ADOPT_STATUS = 2 THEN '채택' WHEN PROPOSAL_ADOPT_STATUS = 3 THEN '모집마감' END AS PROPOSAL_ADOPT_STATUS_STRING FROM (SELECT * FROM PROJECT_PROPOSAL LEFT JOIN (SELECT PROJECT_REQUEST_NO,PROJECT_REQUEST_TITLE, PROJECT_REQUEST_SUMMARY,PROJECT_REQUEST_CONTENT,TO_CHAR(PROJECT_REQUEST_BUDGET, 'FM999,999,999,999') PROJECT_REQUEST_BUDGET_STRING, TO_CHAR(REQUEST_RECRUIT_DATE, 'YYYY.MM.DD') REQUEST_RECRUIT_DATE,TO_CHAR(REQUEST_WORK_DATE, 'YYYY.MM.DD') REQUEST_WORK_DATE, PROJECT_WORK_PERIOD,TO_CHAR(PROJECT_REQUEST_CREATE_DATE, 'YYYY.MM.DD') PROJECT_REQUEST_CREATE_DATE, THIRD_CATEGORY_NO,MEMBER_NO FROM PROJECT_REQUEST) USING (PROJECT_REQUEST_NO) LEFT JOIN CATEGORY USING(THIRD_CATEGORY_NO) LEFT JOIN SUB_CATEGORY USING(SUB_CATEGORY_NO) LEFT JOIN MAIN_CATEGORY USING(MAIN_CATEGORY_NO) LEFT JOIN REQUEST_FILE USING(PROJECT_REQUEST_NO)) p WHERE FREELANCER_NO = #{memberNo} AND REQUEST_FILE_ORDER = 1
AND MAIN_CATEGORY_NO = #{mainCategoryNo}
ORDER BY PROJECT_REQUEST_NO

SELECT COUNT(*) FROM (SELECT * FROM PROJECT_PROPOSAL LEFT JOIN (SELECT PROJECT_REQUEST_NO,PROJECT_REQUEST_TITLE, PROJECT_REQUEST_SUMMARY,PROJECT_REQUEST_CONTENT,TO_CHAR(PROJECT_REQUEST_BUDGET, 'FM999,999,999,999') PROJECT_REQUEST_BUDGET_STRING, TO_CHAR(REQUEST_RECRUIT_DATE, 'YYYY.MM.DD') REQUEST_RECRUIT_DATE,TO_CHAR(REQUEST_WORK_DATE, 'YYYY.MM.DD') REQUEST_WORK_DATE, PROJECT_WORK_PERIOD,TO_CHAR(PROJECT_REQUEST_CREATE_DATE, 'YYYY.MM.DD') PROJECT_REQUEST_CREATE_DATE, THIRD_CATEGORY_NO,MEMBER_NO FROM PROJECT_REQUEST) USING (PROJECT_REQUEST_NO) LEFT JOIN CATEGORY USING(THIRD_CATEGORY_NO) LEFT JOIN SUB_CATEGORY USING(SUB_CATEGORY_NO) LEFT JOIN MAIN_CATEGORY USING(MAIN_CATEGORY_NO) LEFT JOIN REQUEST_FILE USING(PROJECT_REQUEST_NO)) p WHERE FREELANCER_NO = #{memberNo} AND REQUEST_FILE_ORDER = 1
AND MAIN_CATEGORY_NO = #{mainCategoryNo}
ORDER BY PROJECT_REQUEST_NO

  	SELECT  p.*,TO_CHAR(PAYMENT_DATE,'YYYY-MM-DD hh:mm') PAYMENT_DATE_STRING,
		TO_CHAR((PAYMENT_PRICE), 'FM999,999,999,999') PAYMENT_PRICE_STRING,
		TO_CHAR((SERVICE_PRICE), 'FM999,999,999,999') SERVICE_PRICE_STRING,
		ROWNUM NUM
			FROM (SELECT * FROM SETTLEMENT
					JOIN TRADE USING(TRADE_NO)
					JOIN SERVICE USING(SERVICE_NO)
					WHERE FREELANCER_NO = #{memberNo}
					AND WORK_STATUS IN (3)      
					AND PAYMENT_TYPE IN (3)
					AND USER_NO = #{memberNo}  

						 AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') >= #{startDate}


						 AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') <= #{endtDate}


						 AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN #{startDate} AND #{endtDate}

					UNION 
					SELECT * FROM SETTLEMENT
					JOIN TRADE USING(TRADE_NO)
					JOIN SERVICE USING(SERVICE_NO)
					WHERE FREELANCER_NO  =#{memberNo}
					AND WORK_STATUS IN (2)    
					AND PAYMENT_TYPE IN (2)

						 AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') >= #{startDate}


						 AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') <= #{endtDate}


						 AND TO_DATE(to_char(PAYMENT_DATE,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN #{startDate} AND #{endtDate}

					) p 
				ORDER BY PAYMENT_DATE

