mapper.select:

  		SELECT * FROM USER_INQUIRY_FILE
		WHERE USER_INQUIRY_NO = #{userInquiryNo}
		ORDER BY INQUIRY_FILE_ORDER

mapper.update:
UPDATE USER_INQUIRY SET
USER_INQUIRY_REQUEST  = #{inputComment},
USER_INQUIRY_REQUEST_DATE  = SYSDATE,
USER_INQUIRY_REQUEST_MEMBER_NO  = #{memberNo}
WHERE USER_INQUIRY_NO  = #{userInquiryNo}

mapper.select:
SELECT COUNT(*) FROM USER_INQUIRY WHERE MEMBER_NO = #{memberNo}
AND USER_INQUIRY_REQUEST IS NOT NULL
AND USER_INQUIRY_REQUEST IS NULL

SELECT USER_INQUIRY_NO, USER_INQUIRY_TITLE, USER_INQUIRY_REQUEST, TO_CHAR(USER_INQUIRY_CREATE_DATE, 'YYYY-MM-DD'), CASE WHEN USER_INQUIRY_REQUEST IS NULL THEN '답변 대기' WHEN USER_INQUIRY_REQUEST IS NOT NULL THEN '답변 완료' END AS USER_INQUIRY_REQUEST_STATUS FROM USER_INQUIRY WHERE MEMBER_NO = #{memberNo}
AND USER_INQUIRY_REQUEST IS NOT NULL
AND USER_INQUIRY_REQUEST IS NULL
