  			   TO_CHAR(USER_INQUIRY_CREATE_DATE, 'YYYY-MM-DD') USER_INQUIRY_CREATE_DATE
		FROM USER_INQUIRY WHERE MEMBER_NO = #{memberNo}


  		SELECT * FROM USER_INQUIRY_FILE
